    private void btnEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEntrarActionPerformed
        Tela2 = new Tela2();
        for (int i = 0; i < 3; i++) {
        try {
            
             login = txtLogin.getText();
             senha = Integer.parseInt(String.valueOf(pwdSenha.getPassword()));
             
        if(login.equals(loginDb)&& senha == senhaDb){
            // Abrir uma nova janela
            Tela2.setVisible(true);
            setVisible(false);
        }else{
            JOptionPane.showMessageDialog(rootPane, "Login e/ou senha incorretos!");
            limpar();
            contador++;
        }
           
         
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(rootPane, "A senha sÃ³ pode ter numeros!");
            limpar();
            contador++;
        }}
        if(contador == 3){btnEntrar.setEnabled(false);}
    }//GEN-LAST:event_btnEntrarActionPerformed

    private void limpar(){
        txtLogin.setText("");
        pwdSenha.setText("");
    }
    
